  // 添加坐标
  addNodeYX = (
    treeData,
    source,
    { spacingX = 200, spacingY = 300, parentData = {} } = {}
  ) => {
    return treeData.map((item) => {
      let { x, y, xy, children = [] } = item;
      let leftChildren = [];
      let rightChildren = [];
      let middleChildren = [];
      if (children.length >= 2) {
        if (children.length % 2 == 0) {
          // 偶数
          leftChildren = children.slice(0, children.length / 2);
          rightChildren = children.slice(children.length / 2, children.length);
        } else {
          //奇数
          leftChildren = children.slice(0, Math.floor(children.length / 2));
          middleChildren = children.slice(
            Math.floor(children.length / 2),
            Math.floor(children.length / 2) + 1
          );
          rightChildren = children.slice(
            Math.ceil(children.length / 2),
            children.length
          );
        }
      } else if (children.length == 1) {
        //一条线的时候
        middleChildren = [children[0]];
      }
      let newChilern = [];
      let leftIndex = 0;
      for (let index = leftChildren.length - 1; index >= 0; index--) {
        leftIndex += 1;
        if (index == leftChildren.length - 1 && !middleChildren.length) {
          let { parentKeys = [] } = leftChildren[index];
          parentKeys = [...new Set(parentKeys)];
          let xs = [];
          let ys = [];
          if (parentKeys.length >= 2) {
            // 多个父节点
            for (let value of parentKeys) {
              let { x, y } = this.findTreeData(source, value, "key");
              x !== undefined && xs.push(x);
              y !== undefined && ys.push(y);
            }
            if (xs.length && ys.length) {
              let xMax = Math.max.apply(Math, xs);
              let xMin = Math.min.apply(Math, xs);
              let yMax = Math.max.apply(Math, ys);
              x = xMin < 0 ? 0 - (xMax - xMin) / 2 : (xMax - xMin) / 2;
              y = yMax;
            }
          }

          newChilern.unshift({
            ...leftChildren[index],
            x: xs.length ? x : +x - spacingX / 2,
            y: +y + spacingY,
            xy: `${+x - spacingX / 2} ${+y + spacingY}`,
          });
        } else {
          let { parentKeys = [] } = leftChildren[index];
          parentKeys = [...new Set(parentKeys)];
          let xs = [];
          let ys = [];
          if (parentKeys.length >= 2) {
            // 多个父节点
            for (let value of parentKeys) {
              let { x, y } = this.findTreeData(source, value, "key");
              x !== undefined && xs.push(x);
              y !== undefined && ys.push(y);
            }
            if (xs.length && ys.length) {
              let xMax = Math.max.apply(Math, xs);
              let xMin = Math.min.apply(Math, xs);
              let yMax = Math.max.apply(Math, ys);
              x = xMin < 0 ? 0 - (xMax - xMin) / 2 : (xMax - xMin) / 2;
              y = yMax;
            }
          }
          newChilern.unshift({
            ...leftChildren[index],
            x: xs.length ? x : +x - leftIndex * spacingX,
            y: y + spacingY,
            xy: `${+x - leftIndex * spacingX} ${y + leftIndex * spacingY}`,
          });
        }
      }
      for (let index = middleChildren.length - 1; index >= 0; index--) {
        let { parentKeys = [] } = middleChildren[index];
        parentKeys = [...new Set(parentKeys)];
        let xs = [];
        let ys = [];
        if (parentKeys.length >= 2) {
          // 多个父节点
          for (let value of parentKeys) {
            let { x, y } = this.findTreeData(source, value, "key");
            x !== undefined && xs.push(x);
            y !== undefined && ys.push(y);
          }
          if (xs.length && ys.length) {
            let xMax = Math.max.apply(Math, xs);
            let xMin = Math.min.apply(Math, xs);
            let yMax = Math.max.apply(Math, ys);
            x = xMin < 0 ? 0 - (xMax - xMin) / 2 : (xMax - xMin) / 2;
            y = yMax;
          }
        }
        newChilern.push({
          ...middleChildren[index],
          x: xs.length ? x : +x,
          y: y + spacingY,
          xy: `${+x} ${y + spacingY}`,
        });
      }
      for (let index = 0; index < rightChildren.length; index++) {
        if (index == 0 && !middleChildren.length) {
          let { parentKeys = [] } = rightChildren[index];
          parentKeys = [...new Set(parentKeys)];
          let xs = [];
          let ys = [];
          if (parentKeys.length >= 2) {
            // 多个父节点
            for (let value of parentKeys) {
              let { x, y } = this.findTreeData(source, value, "key");
              x !== undefined && xs.push(x);
              y !== undefined && ys.push(y);
            }
            if (xs.length && ys.length) {
              let xMax = Math.max.apply(Math, xs);
              let xMin = Math.min.apply(Math, xs);
              let yMax = Math.max.apply(Math, ys);
              x = xMin < 0 ? 0 - (xMax - xMin) / 2 : (xMax - xMin) / 2;
              y = yMax;
            }
          }
          newChilern.push({
            ...rightChildren[index],
            x: xs.length ? x : +x + spacingX / 2,
            y: y + spacingY,
            xy: `${+x + spacingX / 2} ${y + spacingY}`,
          });
        } else {
          let { parentKeys = [] } = rightChildren[index];
          parentKeys = [...new Set(parentKeys)];
          let xs = [];
          let ys = [];
          if (parentKeys.length >= 2) {
            // 多个父节点
            for (let value of parentKeys) {
              let { x, y } = this.findTreeData(source, value, "key");
              x !== undefined && xs.push(x);
              y !== undefined && ys.push(y);
            }
            if (xs.length && ys.length) {
              let xMax = Math.max.apply(Math, xs);
              let xMin = Math.min.apply(Math, xs);
              let yMax = Math.max.apply(Math, ys);
              x = xMin < 0 ? 0 - (xMax - xMin) / 2 : (xMax - xMin) / 2;
              y = yMax;
            }
          }
          newChilern.push({
            ...rightChildren[index],
            x: xs.length ? x : +x + leftIndex * spacingX,
            y: y + spacingY,
            xy: `${+x + leftIndex * spacingX} ${y + spacingY}`,
          });
        }
      }

      return {
        ...item,
        children: this.addNodeYX(newChilern, source),
      };
    });
  };

  addLinkYX = (linkDataArray, treeData) => {
    return linkDataArray.map((item) => {
      const { from, to } = item;
      if (to) {
        delete item.points;
      } else {
        const { x, y } = this.findTreeData(treeData, from, "key");
        item.points = [x, y + 38, x, y + 150, x, y + 150];
      }
      return item;
    });
  };
  // 搜索到树数据的某一条数据单条 不包括父层数据的
  findTreeData = (
    treeData, // 树形数组或者数组数据
    value, // 需要查找的value
    key, //需要查找数组对象的key
    findValue = null, //获取到的值，这个不用传
    nextKey = "children" // 下一级的key，这个不用传
  ) => {
    for (let item of treeData) {
      if (value != undefined && item[key] != undefined && item[key] == value) {
        return item;
      }
      if (item && item[nextKey] && item[nextKey].length >= 1) {
        findValue = this.findTreeData(
          item[nextKey],
          value,
          key,
          findValue,
          nextKey
        );
      }
    }
    return findValue;
  };
  // 转换成树
  transformTrre = (nodeDataArray) => {
    let treeData = [];
    nodeDataArray = [...nodeDataArray];
    for (let x = 0; x < nodeDataArray.length; x++) {
      const { key: xKey, parentKey: xParentKey } = nodeDataArray[x];
      for (let y = x + 1; y < nodeDataArray.length; y++) {
        const { key: yKey, parentKey: yParentKey } = nodeDataArray[y];
        if (xParentKey === undefined) {
          nodeDataArray[x] = {
            ...nodeDataArray[x],
            x: 0,
            y: 0,
            xy: "0 0",
            // parentNode: undefined,
            // parentNodes: [],
          };
          treeData = [nodeDataArray[x]];
        }
        if (yParentKey === undefined) {
          nodeDataArray[y] = {
            ...nodeDataArray[y],
            x: 0,
            y: 0,
            xy: "0 0",
            // parentNode: undefined,
            // parentNodes: [],
          };
          treeData = [nodeDataArray[y]];
        }
        if (xParentKey == yKey) {
          // const parentNode = nodeDataArray.find((item) => {
          //   return item.key == yKey;
          // });
          // const parentNodes = nodeDataArray.filter((item) => {
          //   return [...new Set(nodeDataArray[x].parentKeys)].includes(item.key);
          // });
          nodeDataArray[x] = {
            ...nodeDataArray[x],
            x: undefined,
            y: undefined,
            xy: undefined,
            // parentNode,
            // parentNodes,
          };
          nodeDataArray[y].children = nodeDataArray[y]?.children?.length
            ? [...nodeDataArray[y].children, nodeDataArray[x]]
            : [nodeDataArray[x]];
        }
        if (yParentKey == xKey) {
          // const parentNode = nodeDataArray.find((item) => {
          //   return item.key == xKey;
          // });
          // const parentNodes = nodeDataArray.filter((item) => {
          //   return [...new Set(nodeDataArray[y].parentKeys)].includes(item.key);
          // });
          nodeDataArray[y] = {
            ...nodeDataArray[y],
            x: undefined,
            y: undefined,
            xy: undefined,
            // parentNode,
            // parentNodes,
          };
          nodeDataArray[x].children = nodeDataArray[x]?.children?.length
            ? [...nodeDataArray[x].children, nodeDataArray[y]]
            : [nodeDataArray[y]];
        }
      }
    }
    return treeData;
  };
  // 点平树
  flatTree = (treeData) => {
    return treeData.reduce((acc, next) => {
      const { children } = next;
      const newNode = {
        ...next,
      };
      delete newNode.children;
      acc = [
        ...acc,
        {
          ...newNode,
        },
      ];
      if (children.length) {
        acc = [...acc, ...this.flatTree(children)];
      }
      return acc;
    }, []);
  };
